config.script:
var projectsLabel="Projects";
var navigationLink=null;
var description=null;
var editBtn=null;

var list=[];

if(item instanceof ProjectList){
    projectsLabel=item.getLabel();

    //console.error(item);

    var tag=item.getCategoryLockFilter();
    if(tag&&!tag.isRootTag()){
        var parentTag=tag.getParentTagData();
        navigationLink=function(){
            UIInteraction.navigateToNamedCategoryType(parentTag.getName());
        }
    }


    if(tag){

        description=tag.getDescription();
        if(typeof description=='string'){
            description=description.split('<').shift();
        }

        if(description&&description!==""){
           description=new Element('p', {html:description, style:'margin: 10px 0 20px 60px;'});
        }else{
            description=null;
        }

        if ((AppClient.getUserType() == "admin" || ProjectTeam.CurrentTeam().getUser(AppClient.getId()).isSiteAdmin())) {



            editBtn=new Element("button", {
                "data-lbl":"Edit",
                "html":"Edit",
                "class": "inline-edit"
            });

            new UIModalFormButton(editBtn, application, tag, {

    			label: "Edit",
    			formOptions: {
    				template: "form"
    			},
    			formName: "tagForm",
    			"class": "inline-edit",
                stopPropagation:true

    		})


        }


    }


    if(item.getDescription){
        description=item.getDescription();
        if(typeof description=='function'){
            description.bind(item)(function(str){
                description=str;
            });
        }
    }

}

var icon='';
if(item.getIcon){
    icon='<span class="section-icon" style="background-image:url('+item.getIcon()+'); ';

    if(item.getColor){
        icon+='background-color:'+item.getColor()+'; ';
    }

    icon+='"></span>';
}


var label=new Element('div', {
    "class":item.getLabelClass?item.getLabelClass():"section-title section-padding",
    html:icon+'<span>'+projectsLabel+'</span>'
});


if(navigationLink){
    label.appendChild(new Element('button',{
        "class":"nav-link",
        events:{
            click:navigationLink
        }
    }))
}

label.setAttribute('data-identifier', 'htmlLabel');
if(editBtn){
    label.appendChild(editBtn);
}

if(description){
    return [label,description];
}


return label;
