config.onRenderTextFieldScript:
var check=function(){
    var p=module.getElement().parentNode.parentNode.parentNode;
    if(textField.getValue()==='Report Template'){
        p.addClass('is-report-template')
        return;
    }
     p.removeClass('is-report-template');

};
textField.addEvent('change',check);
module.runOnceOnLoad(function(){ check(); }, 200);
