    return new MockDataTypeItem({
        type:"widget",
        id:454 // this widget
        });
}

return AppClient;

config.initialPostsScript:
if(AppClient.getUserType()!='guest'){
    return null;
}

var updatedItems=NotificationContent.getUpdated();
var createdItems=NotificationContent.getCreated();

var items = [{
        "id": -1,
        "discussion": -1,
        "text": "calc: items.updated",
        "user": 0,
        "metadata": {
            "name":"System",
            "items":updatedItems.map(function(item){
                return {
                        "type": item.getType(),
                        "id": item.getId()
                    };
            })
        },
        "createdDate": updatedItems[0].getModificationDate(),
        "modifiedDate": updatedItems[0].getModificationDate(),
        "readAccess": "public",
        "textHtml": ""
    },{
        "id": -2,
        "discussion": -1,
        "text": "calc: items.created",
        "user": 0,
        "metadata": {
            "name":"System",
            "items":createdItems.map(function(item){
                return {
                        "type": item.getType(),
                        "id": item.getId()
                    };
            })
        },
        "createdDate": createdItems[0].getCreationDate(),
        "modifiedDate": createdItems[0].getCreationDate(),
        "readAccess": "public",
        "textHtml": ""
    }



];

items.sort(function(a, b){ return a.createdDate.localeCompare(b.createdDate)});

items.unshift(

    {
        "id": -2,
        "discussion": -1,
        "text": "rand: tip",
        "user": 0,
        "metadata": {
            "name":"System",
            "items":[{
                        "type": "tip",
                        "text": "You can only view publicly available datasets and collections."
                    }, {
                        "type": "tip",
                        "text": "Use the Map function to view available spatial data."
                    }]

        },
        "createdDate": (new Date()).toLocaleTimeString(),
        "modifiedDate": (new Date()).toLocaleTimeString(),
        "readAccess": "public",
        "textHtml": ""
    }

)

return items;
